function Todo() {
    const[name,setname]=useState();
    const[data,setdata]=useState([]);
    const addaction=(e)=>{
     setname(e.target.value)   
    }
    const addon=()=>{
setdata((val)=>{
    return [...val,name]
})
setname('');
    }
const delitem=(id)=>{

    setdata((val)=>{
      return  val.filter((vals, index)=>{
            return index !== id
        })
    })

}
  return (
  <>
  <div className='main'>
  <div className='container'>
    <div className='row'>
    <div className='col-md-4 col'></div>
    <div className='mt-5 col-md-4 col-12 col d-flex justify-content-center'>
        
    <div className='card '>
        <div className='head text-danger'>
       <p className=''>ToDo</p>
         <p></p>
        </div>
        <div className='input-item'>
        <input type='text' value={name} onChange={addaction} />
        <button className='btn btn-add' onClick={addon}>Add</button>
        </div>
        <div >
            <ul className='item' type="none">
            {
    data.map((vals,ind)=>{
        return  <li className='list-item' key={ind}><p className='card-text'>{vals}</p> <button className='btn btn-danger' onClick={()=>{delitem(ind)}}>te</button></li>
    })
}
               
               
                
            </ul>
           
        </div>
        <div className='Footer '>
           <button>Clear</button>
        </div>
    </div>
  </div>
  <div className='col-md-4 col'></div>
    </div>
  </div>
  </div>
  </>
  )
}

export default Todo;
