import React, { useState } from 'react'

function Todo() {
    const[name,setname]=useState();
    const[data,setdata]=useState([]);
    const addaction=(e)=>{
     setname(e.target.value)   
    }
    const addon=()=>{
setdata((val)=>{
    return [...val,name]
})
setname('');
    }
const delitem=(id)=>{

    setdata((val)=>{
      return  val.filter((vals, index)=>{
            return index !== id
        })
    })

}
  return (
  <>
  <div className='main'>
    <div className='card'>
        <input type='text' onChange={addaction} value={name}/>
        <button className='btn' onClick={addon}>Add</button>
        <div>
            <ul>
                {
                    data.map((vals,ind)=>{
                        return <li key={ind}>  {vals}<button className='btn'  style={{marginLeft:'20px'}} onClick={()=>{delitem(ind)}}>Del</button></li>
                    })
                }
            </ul>
        </div>
    </div>
  </div>
  </>
  )
}

export default Todo;
